var _ = require('lodash');
var assert = require('assert');

var DB = require('col-core/lib/db');
// var RestAPI = require('col-rest');
var TestsUtil = require('col-tests');
var UsersTestsUtil = require('col-users/tests/util');

describe('Assets', function() {

  describe('Create new assets', function() {

    describe('Links', function() {

      /**
       * Test that verifies that a new link asset can be created
       */
      it('can be created', function(callback) {
        TestsUtil.getAssetLibraryClient(null, null, null, function(client, course, user) {
          UsersTestsUtil.assertGetMe(client, course, null, function(me) {

            var client = TestsUtil.getAnonymousClient();
            client.assets.bookmarklet.createLink(course, me.id, me.bookmarklet_token, 'Google', 'http://www.google.com', {'source': 'http://www.cnn.com'}, function(err, asset) {
              assert.ok(!err);
              assert.ok(asset);
              // console.log(asset);
              return callback();
            });
          });
        });
      });
    });
  });
});
